mapper.delete:
delete from price
where securityId = #{securityId,jdbcType=CHAR}
  and Date = #{date,jdbcType=CHAR}

mapper.insert:
insert into price (securityId, Date, Price
  )
values (#{securityId,jdbcType=CHAR}, #{date,jdbcType=CHAR}, #{price,jdbcType=REAL}
  )

mapper.update:
update price
set Price = #{price,jdbcType=REAL}
where securityId = #{securityId,jdbcType=CHAR}
  and Date = #{date,jdbcType=CHAR}

mapper.select:
select securityId, Date, Price
from price
where securityId = #{securityId,jdbcType=CHAR}
  and Date = #{date,jdbcType=CHAR}
