mapper.insert:
insert into security (securityId, securityName, typeId
  )
values (#{securityId,jdbcType=CHAR}, #{securityName,jdbcType=CHAR}, #{typeId,jdbcType=INTEGER}
  )

mapper.update:
update security
set securityName = #{securityName,jdbcType=CHAR},
  typeId = #{typeId,jdbcType=INTEGER}
where securityId = #{securityId,jdbcType=CHAR}

mapper.select:
select securityId, securityName, typeId
from security
where securityId = #{securityId,jdbcType=CHAR}
