mapper.update:
update portfolio
set portfolioName = #{portfolioName,jdbcType=CHAR},
  positionId = #{positionId,jdbcType=CHAR},
  ownerId = #{ownerId,jdbcType=CHAR}
where portfolioId = #{portfolioId,jdbcType=CHAR}

mapper.select:
select portfolioId, portfolioName, positionId, ownerId
from portfolio
where portfolioId = #{portfolioId,jdbcType=CHAR}
