mapper.delete:
delete from pos
where portfolioId = #{portfolioId,jdbcType=CHAR}
  and securityId = #{securityId,jdbcType=CHAR}
  and updateTime = #{updateTime,jdbcType=CHAR}

mapper.update:
update pos
set position = #{position,jdbcType=REAL}
where portfolioId = #{portfolioId,jdbcType=CHAR}
  and securityId = #{securityId,jdbcType=CHAR}
  and updateTime = #{updateTime,jdbcType=CHAR}

mapper.select:
select portfolioId, securityId, updateTime, position
from pos
where portfolioId = #{portfolioId,jdbcType=CHAR}
  and securityId = #{securityId,jdbcType=CHAR}
  and updateTime = #{updateTime,jdbcType=CHAR}
