mapper.delete:
delete from price
where security_id = #{securityId,jdbcType=CHAR}
  and value_date = #{valueDate,jdbcType=CHAR}

mapper.insert:
insert into price (security_id, value_date, value
  )
values (#{securityId,jdbcType=CHAR}, #{valueDate,jdbcType=CHAR}, #{value,jdbcType=DECIMAL}
  )

mapper.update:
update price
set value = #{value,jdbcType=DECIMAL}
where security_id = #{securityId,jdbcType=CHAR}
  and value_date = #{valueDate,jdbcType=CHAR}

mapper.select:
select security_id, value_date, value
from price
where security_id = #{securityId,jdbcType=CHAR}
  and value_date = #{valueDate,jdbcType=CHAR}
