mapper.select:
select security_id, security_name, security_type_name from security s left join security_classify c on s.security_type_id = c.security_type_id where 1=1
and security_type_name = #{securityType,jdbcType=VARCHAR}
and security_name like concat('%',#{search,jdbcType=VARCHAR},'%')
limit #{page,jdbcType=INTEGER} , #{pageSize,jdbcType=INTEGER}

mapper.insert:
insert into security (security_id, security_name, security_type_id
  )
values (#{securityId,jdbcType=CHAR}, #{securityName,jdbcType=CHAR}, #{securityTypeId,jdbcType=INTEGER}
  )

mapper.update:
update security
set security_name = #{securityName,jdbcType=CHAR},
  security_type_id = #{securityTypeId,jdbcType=INTEGER}
where security_id = #{securityId,jdbcType=CHAR}
